import re

def clean_top_text(raw_text):
    """Remove ANSI escape sequences from text."""
    clean_data = re.sub(r'\x1B(?:[@-Z\\-_]|\[[0-?]*[ -/]*[@-~])', '', raw_text)
    return clean_data


